import React, { Component } from "react";
import API from "../utils/API";

class SearchFunction extends Component {
    state = {
        result: {},
        search: ""
    };

    componentDidMount() {
        this.searchDogs("");
    }

    searchDogs = query => {
        API.search(query).then(res => this.setState({ result: res.data })).catch(err => console.log(err));
    };

    handleInputChange = event => {
        const value = event.target.value;
        const name = event.target.name;
        this.setState({
            [name]: value
        });
    };

    handleFormSubmit = event => {
        event.preventDefault();
        this.searchDogs(this.state.search);
    }

    render() {
        return (
            <div className="container"><br></br>
                <h1>Search for different Breeds of Dogs</h1>
                <form>
                    <div class="form-group">
                        <label for="formGroupExampleInput">Search for Pups</label>
                        <input type="text" class="form-control" id="formGroupExampleInput"
                        value={this.state.search}></input>
                        <div class="form-group">
                            <label for="exampleFormControlSelect1">Example select</label>
                            <select class="form-control" id="exampleFormControlSelect1">
                                <option>1</option>
                                <option>2</option>
                                <option>3</option>
                                <option>4</option>
                                <option>5</option>
                            </select>
                        </div>
                        <br></br>
                        <button onClick={props.handleFormSubmit} type="button" class="btn btn-primary">Search</button>
                    </div>
                </form>
                <div>
                    <img alt="..." className="img-fluid" src={props.message}></img>
                </div>
            </div>
        );
    }
}

export default SearchFunction;
